import React from 'react'
import { Link } from 'react-router-dom'


export const Table = ({THead, TBody, ActionButton={} })=>{
console.log(ActionButton)
    return(
        <div>
            <div className="z-depth-4">
            <table className="responsive-table centered bordered">
                                    <thead>
                                    <tr>
                                        {
                                            THead.map((THcontent , index) => {
                                                return(
                                                    <th>{THcontent}</th>
                                                )
                                            })

                                        }
                                        {
                                            Object.keys(ActionButton).length !== 0 ? <th>Action</th>:null
                                        } 
                                    </tr>
                                    </thead>

                                    {
                                        
                                        TBody.map((TBodyData , index) => {
                                            var Column = Object.keys(TBodyData).map(function(key) {
                                                return TBodyData[key];
                                              });
                                            //   var hi = Object.keys(ActionButton).map(function(key, i) {
                                            //     // use keyName to get current key's name
                                            //     // and a[keyName] to get its value
                                            //     // console.log(keyName),
                                            //     // console.log(keyIndex)
                                            //     return (
                                            //         <div>
                                            //             <Link 
                                            //             to={{pathname :`/operations/country/update`,
                                            //             state: {countryId:Column[0]}
                                            //                 }}><i className="material-icons">edit</i>
                                            //             </Link>
                                            //             <i className="material-icons small">more_vert</i>
                                            //         </div>
                                                    

                                            //     )
                                            //   })
                                            //   console.log(hi)
                                            return (
                                                <tbody key = {Column[0]} className = {Column[0]}>
                                                <tr >
                                                    <td>{Column[1]}</td>
                                                    <td>{Column[2]}</td>
                                                    <td>
                                                        {
                                                          Object.keys(ActionButton).map((Button) => {
                                                              const ButtonProperty = ActionButton[Button];
                                                              let ButtonHTML
                                                              if(ButtonProperty.Link !== undefined){
                                                                  
                                                                    ButtonHTML = 
                                                                        <Link 
                                                                            to={{pathname :ButtonProperty.Link,
                                                                            state: {countryId:Column[ButtonProperty.StateColumn]}
                                                                            }}
                                                                            ><i className="material-icons">{ButtonProperty.ActiveIcon}</i>
                                                                        </Link>
                                                                        
                                                                
                                                              }else if(ButtonProperty.Toggle === true){
                                                                ButtonHTML = 
                                                                    Column[ButtonProperty.BaseColumn] ? 
                                                                    <i className="material-icons" style={{cursor:'pointer'}} onClick={()=>ButtonProperty.clickF()}>{ButtonProperty.HandlingFunction}</i>
                                                                    :
                                                                    <i className="material-icons" style={{cursor:'pointer'}} >{ButtonProperty.InactiveIcon}</i>;
                                                                    
                                                                        
                                                              }else{

                                                              }
                                                              return(
                                                                  < >
                                                                  
                                                                   {ButtonHTML}
                                                                   {
                                                                       ButtonProperty.Separator ? 
                                                                       <i className="material-icons small">more_vert</i>
                                                                       :
                                                                       null
                                                                   }
                                                                
                                                                   </>
                                                              )
                                                          })  
                                                        }
                                                        </td>
                                                    
                                                </tr>
                                                </tbody>
                                                );
                                            
                                        })
                                    }
                                </table>
            </div>
        <div/>
        </div>
    )
}
