import React, { Component } from 'react'
import { UserService} from './../../../../services/userService'
import { Topnav, SideBar, Table, Notification } from './../../../../utils/Util'
import { Link } from 'react-router-dom'

class adminIndex extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             users:[]
        }
    }

    componentWillMount = async ()=>{
        const usersList = await UserService.getUser()
        this.setState({users: usersList})

    }
    
    render() {
        const items = this.state.users.slice(0, 2).map(
            (item) => item
          )
          console.log(items)
        return (
            <div>
                <Topnav/>
                <SideBar/>
                <div className="row">
                            <div className="col s9 offset-s3">
                                <h5>Admin</h5>
                                <br/>
                                <button className="waves-effect waves-light btn red">
                                    <Link 
                                        to={{pathname :`/operations/users/admin/add`, state: {Id:""}}}> 
                                        <i className="material-icons">add</i>
                                    </Link>
                                </button>
                                <a  href={'/operations/users/admin/add'}><button className="waves-effect waves-light btn red">
                                <i className="material-icons">add</i>
                                </button></a>
                                <br/><br/>
                                <Table THead = {['Name', 'User Name', 'E-Mail', 'Contact No.', 'Gender', 'Date Of Joining']} TBody = {this.state.users}/>
                            </div>
                    </div>
            </div>
        )
    }
}

export default adminIndex
