import React, { Component } from 'react'
import { countryModule } from './../../../../api/country'
import TopNav from '../../../navbar/Topnav';
import Sidebar from '../../../navbar/SideNav';
import { Link } from 'react-router-dom'

class Country extends Component {
constructor(props) {
    super(props)

    this.state = {
        country:"" 
    }
}

async componentWillMount(){

    const countryList = await countryModule.getCountry();
    this.setState({country:countryList})
}


    render() {
        const country = this.state.country
        console.log("1")

        if(!Object.keys(country).length){
            return (<div><p>No Data Available</p></div>);
        }
        return (
            <div>
                <TopNav/>
                <div className="row">
                    <div className="col s3">
                    <Sidebar/>
                    </div>

                    <div className="col s9 table_container">
                    <button className="btn  btn-light btn-sm my-0">
            <Link 
                  to={{pathname :`/signup` 
                      }}> Sign UP
            </Link>
          </button>
                <table className ="table">
                <thead className ="">
            <tr>
              <th>Country Name</th>
              <th>Action</th>
            </tr>
          </thead>
                {
                    
                    country.map((country , index) => {
                        return (
                            <tbody key = {country.id} className = {country.id}>
                              <tr >
                                <td>{country.country_name}</td>
                                <td>
                                    <Link 
                                      to={{pathname :`/operations/admin/masters/country/update`,
                                      state: {countryId:country.id}
                                          }}><i className="material-icons">edit</i>
                                    </Link>
                                    <i className="material-icons small">more_vert</i>
                                    <Link 
                                      to={{pathname :`/operations/admin/masters/country/delete`,
                                      state: {countryId:country.id}
                                          }}><i className="material-icons">delete</i>
                                    </Link>
                                </td>
                              </tr>
                            </tbody>
                            );
                        
                    })


                }

          </table>
          
                    </div>
                </div>
                
                
            </div>
        )
    }
}

export default Country
