import React, { Component } from 'react'
import { countryModule } from './../../../../api/country'
import TopNav from '../../../navbar/Topnav';
import Sidebar from '../../../navbar/SideNav';


class Country extends Component {
constructor(props) {
    super(props)

    this.state = {
        cntry:"" 
    }
}

async componentWillMount(){

    const country = await countryModule.getCountry();
    console.log(country)
    this.setState({cntry:country})
    this.setState({
        cntry:
        country.map((x) => ({
            id:x.id,
            country_name: x.country_name,
            is_active: x.is_active
          }))
       })
    console.log('Heyyyyyyyy')
    console.log(this.state)
    
    
}


    render() {
        const cntryy = this.state.cntry
        console.log(cntryy)
        return (
            <div>
                <TopNav/>
                <Sidebar/>
                <table className ="table table-bordered  table-hover">
                <thead className ="black white-text">
            <tr>
              <th>Country Name</th>
              <th>Status</th>
              
            </tr>
          </thead>
                {
                    cntry.map((cntry , index) => {
                        return (
                            <tbody key = {cntry.id} className = {cntry.id}>
                              <tr >
                                <th scope="row">{index + 1}</th>
                                <td>{cntry.country_name}</td>
                                <td>{cntry.is_active}</td>
                          
                              </tr>
                            </tbody>
                            );
                        
                    })


                }

          </table>
            </div>
        )
    }
}

export default Country
