import React, { Component } from 'react'
import { countryModule } from './../../../../api/country'
import TopNav from '../../../navbar/Topnav';
import Sidebar from '../../../navbar/SideNav';


class Country extends Component {
constructor(props) {
    super(props)

    this.state = {
        cntry:"" 
    }
}

async componentWillMount(){

    const country = await countryModule.getCountry();
    console.log(country)
    this.setState({cntry:country})
    this.setState({
        cntry:
        country.map((x) => ({
            id:x.id,
            country_name: x.country_name,
            is_active: x.is_active
          }))
       })
    // console.log('Heyyyyyyyy')
    // console.log(this.state)
    
    
}


    render() {
        // console.log('llllllllllllllllllllllllllllllllllll')
        // const cntry = this.state
        // console.log(this.state.cntry)
        // console.log(cntry)
        var con = this.state.cntry
        console.log(con)
        var persons = [
            {id:"1", firstname : "Malcom", lastname: "Reynolds"},
            {id:"2", firstname : "Kaylee", lastname: "Frye"},
            {id:"3", firstname : "Jayne", lastname: "Cobb"}
          ];
          console.log(persons)
        return (
            <div>
                <TopNav/>
                <Sidebar/>
                <table className ="table table-bordered  table-hover">
                <thead className ="black white-text">
            <tr>
              <th>Country Name</th>
              <th>Status</th>
              
            </tr>
          </thead>
                {
                    con.map((prsn , index) => {
                        return (
                            <tbody key = {prsn.id} className = {prsn.id}>
                              <tr >
                                <td>{prsn.country_name}</td>
                                <td>{prsn.is_active}</td>
                          
                              </tr>
                            </tbody>
                            );
                        
                    })


                }

          </table>
            </div>
        )
    }
}

export default Country
