import React, { Component } from 'react'
//import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import TopNav from '../../../navbar/Topnav';
import SideBar from '../../../navbar/SideBar';
import { countryModule } from '../../../../services/countryService'
import { Link } from 'react-router-dom'
import { Notification } from './../../../../utils/notification'
import { Table } from './../../../../utils/table'

class Country extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            country:[] 
        }

        this.notificationDOMRef =  React.createRef();
        this.countryDelete =  this.countryDelete.bind(this);
    }
    
    async componentWillMount(){
        console.log('in idex')
        // if(this.props.location.state.Notification){
        //     console.log(this.props.location.state.Message)
        // }
        const countryList = await countryModule.getCountry();
        console.log(countryList)
        this.setState({country:countryList})
        
        
    }

    componentDidMount = ()=>{
        console.log('in idex2')
        //NotificationGenerator(this.notificationDOMRef.current, true, 'success', 'Country Added Successfully')
    }

    async countryDelete(countryId){
        const res = await countryModule.deleteCountry(countryId)
        const countryList = await countryModule.getCountry()
        window.location.reload();
        //Notification('Info',"Country Deleted Successfully ")
    }


    
    render() {
        const countryList = this.state.country
        const ActionButton = {
            Edit:{
                Show: true,
                ActiveIcon:'edit',
                Link:`/operations/country/update`,
                StateColumn:0,
                StateName:'',
                Separator:true
            },
            Delete:{
                Show: false,
                ActiveIcon:'delete',
                InactiveIcon:'delete',
                HandlingFunction:'',
                Link:'',
                Separator:true
            },
            Status:{
                Show: true,
                Link:'',
                Icon:'block',
                ActiveIcon:'check_circle',
                InactiveIcon:'block',
                BaseColumn:3,
                Toggle:true,
                HandlingFunction:this.clickF,
                Separator:true
            },
            Details:{
                Show: false,
                ActiveIcon:'explore',
                InactiveIcon:'explore',
                Link:'',
                HandlingFunction:''
            }
        }
        console.log(countryList)
        if(!Object.keys(countryList).length){
            return (
                <div>
                    {/* <ReactNotification ref={this.notificationDOMRef} /> */}
                    <TopNav/>
                    <SideBar userType={1}/>
                </div>
            );
        }
        return (
            <div>
               {/* <ReactNotification ref={this.notificationDOMRef} /> */}
               <TopNav/>
               <SideBar userType={2}/>
                    <div className="row">
                            <div className="col s9 offset-s3">
                                <h5>Country List</h5>
                                <br/>
                                <button className="waves-effect waves-light btn red">
                                    <Link 
                                        to={{pathname :`/operations/country/add` ,
                                        state: {countryId:""}
                                            }}> <i className="material-icons">add</i>
                                    </Link>
                                </button>
                                <br/><br/>
                                <Table THead={['Name', 'Status']} TBody={countryList}/>
                            </div> 
                    </div>
            </div>
        )
    }
}

export default Country
