import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";
import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import "react-notifications-component/dist/theme.css";
import TopNav from '../../../navbar/Topnav';
import SideBar from '../../../navbar/SideBar';
import { countryModule } from '../../../../services/countryService'
import { Link } from 'react-router-dom'
 
class Notification extends Component {
  constructor(props) {
    super(props);
    this.dummy = this.dummy.bind(this);
    this.textarea = React.createRef();
    this.somecomponent = React.createRef();
  }

  dummy(){
    this.somecomponent.current.addNotification({
      title: "Awesomeness",
      message: "Awesome Notifications!",
      type: "success",
      insert: "top",
      container: "top-right",
      animationIn: ["animated", "fadeIn"],
      animationOut: ["animated", "fadeOut"],
      dismiss: { duration: 2000 },
      dismissable: { click: true }
    });
  }

  componentDidMount = () => {
    console.log(this.textarea.current)
    console.log(this.somecomponent.current)
    this.dummy()
    this.textarea.current.select();
  };

  render() {
    return (
      <div>
        <textarea
          className="form-control"
          defaultValue="the quick brown fox."
          ref={this.textarea}
        />
        <ReactNotification ref={this.somecomponent}/>
      </div>
    );
  }
}

export default Notification;
