import React from 'react'
import { Link } from 'react-router-dom'


export const Table = ({THead, TBody, ActionButton})=>{
    console.log(THead, TBody)
    console.log(ActionButton)
    Object.keys(ActionButton).map(function(key) {
        console.log(ActionButton[key])
    });
    return(
        <div>
            <div className="z-depth-4">
            <table className="responsive-table centered bordered">
                                    <thead>
                                    <tr>
                                        {
                                            THead.map((THcontent , index) => {
                                                return(
                                                    <th>{THcontent}</th>
                                                )
                                            })

                                        }
                                        {
                                            ActionButton.Display == true ? <th>Action</th>:null
                                        }
                                    </tr>
                                    </thead>

                                    {
                                        
                                        TBody.map((TBodyData , index) => {
                                            var Column = Object.keys(TBodyData).map(function(key) {
                                                return TBodyData[key];
                                              });
                                              var hi = Object.entries(ActionButton).map(function(key, i) {
                                                // use keyName to get current key's name
                                                // and a[keyName] to get its value
                                                // console.log(keyName),
                                                // console.log(keyIndex)
                                                return ([key, i])
                                              })
                                              console.log(hi)
                                            return (
                                                <tbody key = {Column[0]} className = {Column[0]}>
                                                <tr >
                                                    <td>{Column[1]}</td>
                                                    <td>{Column[2]}</td>
                                                    {
                                                        
                                                    }
                                                    <td>
                                                        <Link 
                                                        to={{pathname :`/operations/country/update`,
                                                        state: {countryId:Column[0]}
                                                            }}><i className="material-icons">edit</i>
                                                        </Link>
                                                        <i className="material-icons small">more_vert</i>
                                                        
                                                        {
                                                            Column[2] == 'Active'?
                                                            <i className="material-icons" style={{cursor:'pointer'}} onClick={()=>this.countryDelete(Column[0])}>check_circle</i>:
                                                            <i className="material-icons" style={{cursor:'pointer'}} onClick={()=>this.countryDelete(Column[0])}>block</i>
                                                        }

                                                        {
                                                            ActionButton.Edit.Show ?
                                                            <Link 
                                                                to={{pathname :ActionButton.Edit.Link, state: {countryId:Column[0]} }}>
                                                                <i className="material-icons">{ActionButton.Edit.Icon}</i>
                                                            </Link>
                                                            :
                                                            null
                                                        }
                                                        {
                                                          ActionButton.Edit.Show && ActionButton.Edit.Separator ?
                                                          <i className="material-icons small">more_vert</i>
                                                          :
                                                          null  
                                                        }
                                                        
                                                        
                                                    </td>
                                                </tr>
                                                </tbody>
                                                );
                                            
                                        })
                                    }
                                </table>
            </div>
        <div/>
        </div>
    )
}
