import React, { Component } from 'react'
import { countryModule } from './../../../../api/country'
import TopNav from '../../../navbar/Topnav';
import Sidebar from '../../../navbar/SideNav';


class Country extends Component {
constructor(props) {
    super(props)

    this.state = {
        country:"" 
    }
}

async componentWillMount(){

    const countryList = await countryModule.getCountry();
    this.setState({country:countryList})
}


    render() {
        const country = this.state.country
        console.log("1")

        if(!Object.keys(country).length){
            return (<div><p>No Data Available</p></div>);
        }
        return (
            <div>
                <TopNav/>
                <Sidebar/>
                <div className="row">
                <div className="col s9 offset-s3">
                <table className ="table table-bordered  table-hover">
                <thead className ="black white-text">
            <tr>
                <th>Serial No.</th>
              <th>Country Name</th>
              <th>Status</th>
              <th>Status</th>
              <th>Status</th>
            </tr>
          </thead>
                {
                    
                    country.map((country , index) => {
                        return (
                            <tbody key = {country.id} className = {country.id}>
                              <tr >
                                <th scope="row">{index + 1}</th>
                                <td>{country.country_name}</td>
                                <td>{country.is_active}</td>
                                <td>
                    <button type="button"
                          className="btn btn-danger btn-rounded btn-sm my-0"> Edit<i className="material-icons">edit</i>
                                
                    </button>
                </td>
                <td>
                  <span className="table-remove"><button type="button"
                      className="btn btn-danger btn-rounded btn-sm my-0" 
                      >Delete</button></span>
                </td>
                              </tr>
                            </tbody>
                            );
                        
                    })


                }

          </table>
          </div>
          </div>
            </div>
        )
    }
}

export default Country
