import React, { Component } from 'react'
import Sidebar from './../../../../navbar/SideBar'
import TopNav from '../../../../navbar/Topnav';
import { countryModule } from './../../../../../api/country'
export class AddCountry extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            country_name:"" 
        }

        this.handleChange = this.handleChange.bind(this);
        this.countryCreate =  this.countryCreate.bind(this);
    }

    async countryCreate(event){
        event.preventDefault();
        if(this.state.country_name ){
            const res = await countryModule.addCountry(this.state)
             this.setState({
                fullName: "" ,
                email: "" ,
                mobile: "",
                city: ""
               });
               this.props.history.push("/operations/admin/masters/country");
               //alertmesage.createNotification(200,"Country Created")
            }
            //for redirecting home page
             //this.props.history.push("/");
             //alertmesage.createNotification(res.status,"Employee "+res.data.fullName+" created ")
        // }else{
        //     //Alert message
        //     //alertmesage.createNotification(400,"Did you miss filling some field.")
        // }
    }
    
    handleChange(event){
        this.setState({
            country_name: this.refs.country_name.value 
        })
    }
    
    render() {
        
        return (
            <div>
                <TopNav/>
                <Sidebar/>
                <div  className="row">
                    <div className="col s9 offset-s3">
                            <form>
                                <div className="row">
                                    <div className="input-field col s4 offset-s3">
                                        <input id="contry_name" type="text" className="validate" value={this.state.country_name} onChange={this.handleChange} ref="country_name"/>
                                        <label htmlFor="contry_name">Country</label>
                                    </div>
                                </div>
                                <div className="row col s3 offset-s4">
                                    <button className="btn btn-info btn-block btn-sm" type="submit" onClick={this.countryCreate}>Submit</button>
                                </div>
                            </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default AddCountry
