        axios({
            method: 'post',
            url: apiurl,
            data: bodyFormData,
            withCredentials: true
          })
          .then(response => {
            return response
          })
        .catch(err => {
       
          return err;
        })




    }
}
export default ApiService



export const loginModule = {
  login(data) {
    return ApiService.post( 'http://localhost:3001/signIn',data)
  }
}
