import React, { Component } from 'react'

class addUpdateAdmin extends Component {
    constructor(props) {
        super(props)
        this.validator = new FormValidator([
            { 
                field: 'fname', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'First Name is required.' 
            },
            { 
                field: 'lname', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Last Name is required.' 
            },
            { 
                field: 'uname', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'User Name is required.' 
            },
            { 
                field: 'email', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Email is required.' 
            },
            { 
                field: 'email',
                method: 'isEmail', 
                validWhen: true, 
                message: 'That is not a valid email.'
            },
            { 
                field: 'doj', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'DOJ is required.' 
            },
            { 
                field: 'dob', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'DOB is required.' 
            },
            { 
                field: 'mobile', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Pleave provide a phone number.'
            },
            {
                field: 'mobile', 
                method: 'matches',
                args: [/^\(?\d\d\d\)? ?\d\d\d-?\d\d\d\d$/], // args is an optional array of arguements that will be passed to the validation method
                validWhen: true, 
                message: 'That is not a valid phone number.'
            }

          ]);

        this.state = {
            fname: '',
            lname: '',
            uname: '',
            gender:'',
            email: '',
            mobile: '',
            password: '',
            validation: this.validator.valid() 
        }

        this.handleInputChange = this.handleInputChange.bind(this)
    }

    handleInputChange = event =>{
        this.setState({
            [ event.target.name ] : event.target.value
        })
    }
    
    render() {
        return (
            <div>
                
            </div>
        )
    }
}

export default addUpdateAdmin
