import React, { Component } from 'react'
import { MaterializeCSS } from './../../utils/Util'
import { countryModule } from './../../services/countryService'

export class countryDropDown extends Component {
    constructor(props) {

        super(props)
    
        this.state = {
             countryList:[]
        }
    }

    componentWillMount = async ()=>{
        MaterializeCSS.AutoInit()
        const countryList = await countryModule.getCountry()
        console.log(countryList)
        this.setState({countryList:countryList})
    }
    
    render() {
        if(!Object.keys(this.state.countryList).length){
            return (
                <div>
                    <select name="gender">
                    <option value="" disabled selected>Choose your option</option>
                    </select>
                </div>
            );
        }
        return (
            <div>
                <select name="gender">
                <option value="" disabled selected>Choose your option</option>
                {
                   this.state.countryList.map((countryList , index) => {
                       return(
                        <option key={index} value={countryList.id}>{countryList.country_name}</option>
                        
                       )
                    })
                }
                </select>
                
            </div>
        )
    }
}

export default countryDropDown
